import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';
import { fetchJobs } from '../services/api';

const DashboardContainer = styled.div`
  padding: 2rem;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  min-height: 100vh;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding: 2rem;
  background: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  box-shadow: var(--shadow);
  backdrop-filter: blur(10px);

  h1 {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-size: 2rem;
    margin-bottom: 0.5rem;
  }

  p {
    color: var(--text-color);
    font-size: 1.1rem;
  }
`;

const JobsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 2rem;
  padding: 1rem;
  animation: fadeIn 0.5s ease-out;

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const JobCard = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  padding: 1.5rem;
  box-shadow: var(--shadow);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-10px);
    box-shadow: var(--hover-shadow);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 5px;
    background: linear-gradient(90deg, var(--secondary-color), var(--primary-color));
  }
`;

const CompanyLogo = styled.img`
  width: 60px;
  height: 60px;
  border-radius: 12px;
  margin-bottom: 1rem;
  object-fit: cover;
  border: 3px solid var(--light-gray);
`;

const CompanyName = styled.h3`
  color: var(--primary-color);
  font-size: 1.2rem;
  margin-bottom: 0.5rem;
  font-weight: 600;
`;

const JobTitle = styled.h4`
  color: var(--secondary-color);
  font-size: 1.1rem;
  margin-bottom: 1rem;
  font-weight: 500;
`;

const JobDetail = styled.p`
  color: var(--text-color);
  margin: 0.5rem 0;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  svg {
    color: var(--secondary-color);
  }
`;

const TagsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const Tag = styled.span`
  background: linear-gradient(135deg, var(--secondary-color), #2980b9);
  color: white;
  padding: 0.4rem 0.8rem;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 2px 5px rgba(52, 152, 219, 0.3);
  }
`;

const LogoutButton = styled.button`
  padding: 0.8rem 1.5rem;
  background: linear-gradient(135deg, var(--accent-color), #c0392b);
  color: white;
  border: none;
  border-radius: 10px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(231, 76, 60, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  font-size: 1.2rem;
  color: var(--secondary-color);

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }

  &::after {
    content: '';
    width: 40px;
    height: 40px;
    border: 4px solid #f3f3f3;
    border-top: 4px solid var(--secondary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin-left: 1rem;
  }
`;

function Dashboard() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    } else {
      navigate('/');
    }

    loadJobs();
  }, [navigate]);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const data = await fetchJobs();
      setJobs(data);
      setLoading(false);
    } catch (err) {
      setError('Failed to load jobs. Please try again later.');
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    navigate('/');
  };

  if (loading) {
    return (
      <DashboardContainer>
        <LoadingSpinner>Loading jobs...</LoadingSpinner>
      </DashboardContainer>
    );
  }

  if (error) {
    return (
      <DashboardContainer>
        <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>
      </DashboardContainer>
    );
  }

  return (
    <DashboardContainer>
      <Header>
        <div>
          <h1>Welcome, {user?.name}</h1>
          <p>{jobs.length} jobs found</p>
        </div>
        <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
      </Header>

      <JobsGrid>
        {jobs.map((job) => (
          <JobCard key={job.id}>
            {job.logo && <CompanyLogo src={job.logo} alt={job.company} />}
            <CompanyName>{job.company}</CompanyName>
            <JobTitle>{job.position}</JobTitle>
            <JobDetail>üìç Location: {job.location}</JobDetail>
            <JobDetail>üí∞ Salary: ${job.salary}</JobDetail>
            <JobDetail>‚è∞ Posted: {new Date(job.postedAt).toLocaleDateString()}</JobDetail>
            <TagsContainer>
              {job.requirements.map((req, index) => (
                <Tag key={index}>{req}</Tag>
              ))}
            </TagsContainer>
          </JobCard>
        ))}
      </JobsGrid>
    </DashboardContainer>
  );
}

export default Dashboard;
