import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { usersService } from '../services/users';
import { validateUser } from '../utils/validation';
import images from '../assets/images';
import { formStyles } from '../assets/styles/common';
import { Logo } from '../assets/components/Logo';
import { css } from '@emotion/react';

const PageContainer = styled.div`
  display: flex;
  flex: 1;
`;

const FormSection = styled.div`
  flex: 1;
  padding: 40px;
  display: flex;
  flex-direction: column;
  gap: 30px;
  background-color: ${props => props.theme.colors.white};
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 16px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #2c3e50;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #34495e;
  }
`;

const FormTitle = styled.h2`
  margin: 0;
  color: #2c3e50;
`;

const LoginContainer = styled.div`
  padding: ${props => props.theme.spacing.xl};
  background-color: ${props => props.theme.colors.white};
  border-radius: ${props => props.theme.borderRadius.medium};
  box-shadow: ${props => props.theme.shadows.medium};
`;

function LoginPage() {
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({
    username: '',
    password: ''
  });

  const [signupData, setSignupData] = useState({
    username: '',
    password: '',
    dob: '',
    email: ''
  });

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const user = await usersService.findUserByCredentials(
        loginData.username,
        loginData.password
      );

      if (user) {
        localStorage.setItem('user', JSON.stringify(user));
        navigate('/dashboard');
      } else {
        alert('Invalid credentials!');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('Error during login');
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    
    // Validate user input
    const validation = validateUser(signupData);
    if (!validation.isValid) {
      alert(validation.errors.join('\n'));
      return;
    }

    try {
      const existingUsers = await usersService.getAllUsers();
      
      // Check if user already exists
      const userExists = existingUsers.some(
        user => user.email === signupData.email || user.name === signupData.username
      );

      if (userExists) {
        alert('User with this email or username already exists!');
        return;
      }

      // Calculate age
      const today = new Date();
      const birthDate = new Date(signupData.dob);
      const age = today.getFullYear() - birthDate.getFullYear();

      // Create new user object
      const newUser = {
        id: Date.now(),
        name: signupData.username,
        pwd: signupData.password,
        email: signupData.email,
        DOB: signupData.dob,
        age: age,
        createdAt: new Date().toISOString()
      };

      // Save user
      const saved = await usersService.saveUser(newUser);

      if (saved) {
        setSignupData({
          username: '',
          password: '',
          dob: '',
          email: ''
        });
        alert('Registration successful! Please login.');
      } else {
        alert('Error during registration. Please try again.');
      }

    } catch (error) {
      console.error('Signup error:', error);
      alert('Error during signup');
    }
  };

  return (
    <LoginContainer>
      <div css={formStyles}>
        <PageContainer>
          <FormSection>
            <div>
              <FormTitle>Login</FormTitle>
              <Form onSubmit={handleLogin}>
                <Input
                  type="text"
                  placeholder="Enter Username"
                  value={loginData.username}
                  onChange={(e) => setLoginData({...loginData, username: e.target.value})}
                />
                <Input
                  type="password"
                  placeholder="Enter Password"
                  value={loginData.password}
                  onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                />
                <Button type="submit">Login</Button>
              </Form>
            </div>

            <div>
              <FormTitle>Sign Up</FormTitle>
              <Form onSubmit={handleSignup}>
                <Input
                  type="text"
                  placeholder="Enter Username"
                  value={signupData.username}
                  onChange={(e) => setSignupData({...signupData, username: e.target.value})}
                />
                <Input
                  type="password"
                  placeholder="Enter Password"
                  value={signupData.password}
                  onChange={(e) => setSignupData({...signupData, password: e.target.value})}
                />
                <Input
                  type="date"
                  placeholder="Enter Date of Birth"
                  value={signupData.dob}
                  onChange={(e) => setSignupData({...signupData, dob: e.target.value})}
                />
                <Input
                  type="email"
                  placeholder="Enter Email"
                  value={signupData.email}
                  onChange={(e) => setSignupData({...signupData, email: e.target.value})}
                />
                <Button type="submit">Sign Up</Button>
              </Form>
            </div>
          </FormSection>
        </PageContainer>
      </div>
    </LoginContainer>
  );
}

export default LoginPage;
