import axios from 'axios';

const API_KEY = '7b1fdd81-34a6-466f-a577-837c39e10048';
const APP_ID = '2401e4d6'; // Your Adzuna APP_ID
const BASE_URL = 'https://api.adzuna.com/v1/api/jobs/gb/search/1';

export const fetchJobs = async (searchTerm = '') => {
  try {
    const response = await axios.get(`${BASE_URL}`, {
      params: {
        app_id: APP_ID,
        app_key: API_KEY,
        results_per_page: 20,
        what: searchTerm || 'software developer'
      }
    });
    return response.data.results;
  } catch (error) {
    console.error('Error fetching jobs:', error);
    throw error;
  }
};
