import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';

const PageContainer = styled.div`
  display: flex;
  flex: 1;
`;

const FormSection = styled.div`
  flex: 1;
  padding: 40px;
  display: flex;
  flex-direction: column;
  gap: 30px;
  background-color: white;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 16px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #2c3e50;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #34495e;
  }
`;

const FormTitle = styled.h2`
  margin: 0;
  color: #2c3e50;
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  background-color: #fdeaea;
  padding: 10px;
  border-radius: 4px;
  margin-bottom: 10px;
`;

const SuccessMessage = styled.div`
  color: #27ae60;
  background-color: #e8f5e9;
  padding: 10px;
  border-radius: 4px;
  margin-bottom: 10px;
`;

function LoginPage() {
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({
    username: '',
    password: ''
  });

  const [signupData, setSignupData] = useState({
    username: '',
    password: '',
    dob: '',
    email: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Get users from localStorage
    const users = JSON.parse(localStorage.getItem('users')) || [];
    
    // Find user with matching credentials
    const user = users.find(
      u => u.name === loginData.username && u.pwd === loginData.password
    );

    if (user) {
      // Store logged in user
      localStorage.setItem('currentUser', JSON.stringify(user));
      navigate('/dashboard');
    } else {
      setError('Invalid username or password');
    }
  };

  const handleSignup = (e) => {
    e.preventDefault();
    
    // Validate all fields
    if (!signupData.username || !signupData.password || !signupData.dob || !signupData.email) {
      setError('All fields are required');
      return;
    }

    // Get existing users
    const users = JSON.parse(localStorage.getItem('users')) || [];

    // Check if username or email already exists
    const userExists = users.some(
      user => user.name === signupData.username || user.email === signupData.email
    );

    if (userExists) {
      setError('Username or email already exists');
      return;
    }

    // Calculate age
    const today = new Date();
    const birthDate = new Date(signupData.dob);
    const age = today.getFullYear() - birthDate.getFullYear();

    // Create new user
    const newUser = {
      id: Date.now(),
      name: signupData.username,
      pwd: signupData.password,
      email: signupData.email,
      DOB: signupData.dob,
      age: age
    };

    // Add new user to array
    const updatedUsers = [...users, newUser];

    // Save to localStorage
    localStorage.setItem('users', JSON.stringify(updatedUsers));

    // Clear form and show success message
    setSignupData({
      username: '',
      password: '',
      dob: '',
      email: ''
    });
    setError('');
    setSuccess('Registration successful! Please login.');
  };

  return (
    <PageContainer>
      <FormSection>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        {success && <SuccessMessage>{success}</SuccessMessage>}
        
        <div>
          <FormTitle>Login</FormTitle>
          <Form onSubmit={handleLogin}>
            <Input
              type="text"
              placeholder="Enter Username"
              value={loginData.username}
              onChange={(e) => setLoginData({...loginData, username: e.target.value})}
            />
            <Input
              type="password"
              placeholder="Enter Password"
              value={loginData.password}
              onChange={(e) => setLoginData({...loginData, password: e.target.value})}
            />
            <Button type="submit">Login</Button>
          </Form>
        </div>

        <div>
          <FormTitle>Sign Up</FormTitle>
          <Form onSubmit={handleSignup}>
            <Input
              type="text"
              placeholder="Enter Username"
              value={signupData.username}
              onChange={(e) => setSignupData({...signupData, username: e.target.value})}
            />
            <Input
              type="password"
              placeholder="Enter Password"
              value={signupData.password}
              onChange={(e) => setSignupData({...signupData, password: e.target.value})}
            />
            <Input
              type="date"
              placeholder="Enter Date of Birth"
              value={signupData.dob}
              onChange={(e) => setSignupData({...signupData, dob: e.target.value})}
            />
            <Input
              type="email"
              placeholder="Enter Email"
              value={signupData.email}
              onChange={(e) => setSignupData({...signupData, email: e.target.value})}
            />
            <Button type="submit">Sign Up</Button>
          </Form>
        </div>
      </FormSection>
    </PageContainer>
  );
}

export default LoginPage;
